package main
import (
	"fmt"
	"math"
)

// https://dev.to/seanpgallivan/solution-3sum-with-multiplicity-12ma#idea
func threeSumMulti(arr []int, target int) int {
	nmap := map[int]int{}
	third := int(math.Ceil(float64(target) / 3))
	result := int(0)
	for _, num := range arr {
		nmap[num] += 1
	}
	i := min(target, 100)
	for i >= third {
		remain := target - i
		half := int(math.Ceil(float64(remain) / 2))
		j := min(remain, i)
		for j >= half {
			k := remain - j
			x, y, z := nmap[k], nmap[j], nmap[i]
			if k == i {
				result += x * (x - 1) * (x - 2) / 6
			} else if k == j {
				result += x * (x - 1) / 2 * z
			} else if j == i {
				result += x * y * (y - 1) / 2
			} else {
				result += x * y * z
			}
			j--
		}
		i--
	}
	return result % (1e9 + 7)
}

func min(a, b int) int {
	if b < a {
		return b
	}
	return a
}

// Time Limit Exceeded
func ngSolution(arr []int, target int) int {
	if len(arr) < 3 {
		return 0
	} else if len(arr) == 3 {
		if sum(arr) == target {
			return 1
		}
		return 0
	}


	result := int(0)
	if isSameNums(arr) {
		sumArr := sum(arr)
		if (sumArr / len(arr) * 3 == target) {
			return Binomial(len(arr), 3) % (1e9 + 7)
		}
	}

	for i, num1 := range arr {
		current := []int{num1}
		// result += traversal(current, arr[i + 1:], target)
		j := int(i + 1)
		for j < len(arr) {
			num2 := arr[j]
			copyCurrent := make([]int, len(current))
			copy(copyCurrent, current)

			// fmt.Printf("current = %v, num2 = %v\n", copyCurrent, num2)
			copyCurrent = append(copyCurrent, num2)
			currentSum := sum(copyCurrent)
			if currentSum > target {
				j++
				continue
			}
			remainArr := arr[j + 1:]
			// fmt.Printf("remainArr = %v\n", remainArr)
			remain := target - currentSum
			for _, num3 := range remainArr {
				// fmt.Printf("current = %v, num3 = %v\n", copyCurrent, num3)
				if remain == num3 {
					result++
				}
			}
			j++
		}
	}
	return result % (1e9 + 7)
}

func Binomial(n, k int) int {
	if n < 0 || k < 0 {
		return 0
	}
	if n < k {
		return 0
	}
	// (n,k) = (n, n-k)
	if k > n/2 {
		k = n - k
	}
	b := 1
	for i := 1; i <= k; i++ {
		b = (n - k + i) * b / i
	}
	return b
}

func isSameNums(nums []int) bool {
	if len(nums) == 0 {
		return true
	}
	target := nums[0]
	for _, num := range nums[1:] {
		if target != num {
			return false
		}
	}
	return true
}

func sum(nums []int) int {
	result := int(0)
	for _, num := range nums {
		result += num
	}
	return result
}

