package main
import (
	"fmt"
	"sort"
)

const modulo = 1e9 + 7

func threeSumMulti(arr []int, target int) int {
	m := map[int]int{}
	for _, num := range arr {
		m[num]++
	}

	result := int(0)
	counted := map[string]bool{}
	for n1, count1 := range m {
		for n2, count2 := range m {
			if n1 + n2 > target || (n1 == n2 && count1 < 2) {
				continue
			}
			for n3, count3 := range m {
				three := []int{n1, n2, n3}
				sort.Ints(three)
				key := fmt.Sprintf("%v%v%v", three[0], three[1], three[2])
				if n1 + n2 + n3 != target || counted[key] {
					continue
				}

				// fmt.Printf("result = %v\n", result)
				// fmt.Println(n1, n2, n3)
				// fmt.Println(count1, count2, count3)

				if n1 == n2 {
					if n2 == n3 {
						if count1 < 3 {
							continue
						}
						result += combination(count3, 3)
					} else {
						if count2 < 2 {
							continue
						}
						result += combination(count2, 2) * count3
					}
				} else if n2 == n3 {
					if count2 < 2 {
						continue
					}
					result += combination(count2, 2) * count1
				} else if n1 == n3 {
					if count1 < 2 {
						continue
					}
					result += combination(count1, 2) * count2
				} else {
					result += count1 * count2 * count3
				}

				result = result % modulo
				counted[key] = true
			}
		}
	}

	return result % modulo
}

func combination(n int, r int) int {
	result := int(1)
	for i := n; i > n - r; i-- {
		result *= i
	}
	for i := 1; i <= r; i++ {
		result /= i
	}
	return result
}

